n uke op til 7$day_count = 1;echo "<tr>";//setter inn tomme tabellrader der det ikke er datowhile ( $blank > 0 ){	echo "<td></td>";	$blank = $blank-1;	$day_count++;}//setter første dagen i mnd til 1$day_num = 1;//teller opp dagene til vi kommer til slutten av mndwhile ( $day_num <= $days_in_month ){	echo "<td> $day_num </td>";	$day_num++;	$day_count++;	//starter ny rad for hver uke	if ($day_count > 7)	{		echo "</tr><tr>";		$day_count = 1;	}}// fyller inn blanke områder på slutten hvis det trengs...while ( $day_count >1 && $day_count <=7 ){	echo "<td> </td>";	$day_count++;}echo "</tr></table>";include '../includes/footerVedlikehold.php';?>?>