I I HVER RAD LAGRES I VARIABEL	$start_time=$row[1];								//ANDRE VERDI I HVER RAD LAGRES I VARIABEL	$end_time=$row[2];									//TREDJE VERDI I HVER RAD LAGRES I VARIABEL	$name=$row[3];										//FJERDE VERDI I HVER RAD LAGRES I VARIABEL	$type=$row[4];										//FEMTE VERDI I HVER RAD LAGRES I VARIABEL	$ref_booking=$row[5];								//SJETTE VERDI I HVER RAD LAGRES I VARIABEL	$username=$row[6];									//SYVENDE VERDI I HVER RAD LAGRES I VARIABEL	$userRef=$row[7];									//ÅTTENDE VERDI I HVER RAD LAGRES I VARIABEL		$bookedStart=explode(":", $start_time);				//EKS. STARTTIDSPUNKT 12:00:01 LØSES OPP MED : SOM SKILLETEGN	$bookedStartHour = $bookedStart[0];					//FØRSTE VERDI (EKS 12) TILORNES EN VARIABEL	$bookedStartMinute = $bookedStart[1];				//ANDRE VERDI (EKS. 00) TILORDNES EN VARIABEL (SEKUNDER TRENGER VI IKKE, SOM ER DEN TREDJE VERDIEN)		$bookedEnd=explode(":", $end_time);					//EKS. SLUTTIDSPUNKT 14:00:00 LØSES OPP MED : SOM SKILLETEGN	$bookedEndHour = $bookedEnd[0];						//FØRSTE VERDI (EKS 14) TILORNES EN VARIABEL	$bookedEndMinute = $bookedEnd[1];					//ANDRE VERDI (EKS. 00) TILORDNES EN VARIABEL (SEKUNDER TRENGER VI IKKE, SOM ER DEN TREDJE VERDIEN)	connect();											//KOBLER TIL DATABASEN		$sql = "SELECT Username,Ref_User FROM User							ORDER BY Username ASC;";				//HENTER UT ALLE BRUKERNAVN I DATABASEN		$result = mysql_query($sql) or die ("ikke mulig å hente brukernavn fra databasen");	//KJØRER SPØRRINGEN MOT DATABASEN OG LAGRER RESULTATET I VARIABEL		$numRows = mysql_num_rows($result);				//TELLER ANNTALL RADER I RESULTATET	disconnect();/***********************************Start på form*********************************/echo ("<form class='' name='editBooking' id='editBooking' action='' method='post'>		<fieldset>				<legend>Endre bestilling</legend>				<p>Dato: <strong>" . normalDate($dato) . "</strong></p>		<!--Dato kan ikke endres.-->						<p>RomNr: <strong>$name</strong></p>	<!--RomNr kan ikke endres.-->						<label for='userRef' class='clearfloat'>Bruker bestillingen er registrert på</label>				<select name='userRef' id='userRef'>					<option value='$userRef'>$username</option>");		//HENTER BRUKERNAVN TIL DEN BESTILLINGEN ER REGISTRERT PÅ. HAR VALUE MED BRUKERREFERANSE DA DENNE SKAL BRUKES VIDERE						for($t=1; $t<=$numRows; $t++){						//FOR LØKKE SOM GÅR LIKE MANGE GANGER SOM BRUKERE FUNNET						$row=mysql_fetch_array($result);				// LAGER VARIABEL $ROW SOM INNEHOLDER HELE ARRAYET						$username=$row[0];								//LEGGES SOM SYNLIG TEKST I OPTION						$userRef=$row[1];								//LEGGES SOM VERDI I OPTION. SKAL BRUKE DENNE VIDERE						echo("<option value='$userRef'>$username</option>");						}					echo("</select>				<p>Opprinnelig bestilt tidsrom er fra " . normalTime($start_time) . " til " . normalTime($end_time) .". Endre tidspunkt under.</p>						<label for='startTimeHours' class='clearfloat'>Fra kl</label>				<select name='startTimeHours'>");					$timer = array();														// LAGER ET ARRAY FOR TIMER										connect();																//KOBLER TIL DATABASEN					$sql="SELECT SUBSTRING(Klokka,1,2) as Timer FROM time GROUP BY Timer HAVING COUNT(*);";	// HENTER UT TIMER FRA DATABASEN					$result=mysql_query($sql) or die ("ikke mulig å hente timer fra databasen");			//KJØRER SPØRRINGEN MOT DATABASEN OG LAGRER RESULTATET I VARIABEL					$timerLength=mysql_num_rows($result);									// VARIABEL SOM SIER HVOR MANGE TIMER (RADER) ARRAYET ER					disconnect();															// KOBLER FRA DATABASEN										for($i=0;$i<$timerLength;$i++) {										// FOR LØKKE SOM GÅR LIKE MANGE GANGER SOM TIMER TABELLEN HAR I LENGDE						$rad=mysql_fetch_array($result);									// LAGER VARIABEL $RAD SOM INNEHOLDER HELE ARRAYET						$timer[$i] = $rad[0];												// TILORDNER ALT FRA $RAD ARRAYET TIL TIME ARRAYET						echo "<option value='$rad[0]' "; if($rad[0] == $bookedStartHour) { echo "selected"; } echo ">$rad[0]</option>";		// SKRIVER DEN SPESIFIKKE KOLONNEN FRA $RAD ARRAYET OG LEGGER IN SELECTED HVIS VERDI ER LIK VARIABEL					}				echo ("</select> 										<select name='startTimeMinutes'>");					$minutter = array();													// LAGER ET ARRAY FOR MINUTTER										connect();																//KOBLER TIL DATABASEN					$sql="SELECT SUBSTRING(Klokka,4,2) as Minutter FROM time GROUP BY Minutter HAVING COUNT(*) > 1;";	// HENTER UT MINUTTER FRA DATABASEN					$result=mysql_query($sql) or die ("ikke mulig å hente minutter fra databasen");						//KJØRER SPØRRINGEN MOT DATABASEN OG LAGRER RESULTATET I VARIABEL					$minLength=mysql_num_rows($result);										// VARIABEL SOM SIER HVOR MANGE MINUTTER (RADER) ARRAYET ER					disconnect();															//KOBLER FRA DATABASEN										for($i=0;$i<$minLength;$i++) {											// FOR LØKKE SOM GÅR LIKE MANGE GANGER SOM MINUTTER TABELLEN HAR I LENGDE						$rad=mysql_fetch_array($result);									// LAGER VARIABEL $RAD SOM INNEHOLDER HELE ARRAYET						$minutter[$i] = $rad[0];											// TILORDNER ALT FRA $RAD ARRAYET TIL MINUTT ARRAYET						echo "<option value='$rad[0]' "; if($rad[0] == $bookedStartMinute) { echo "selected"; } echo ">$rad[0]</option>";					// SKRIVER DEN SPESIFIKKE KOLONNEN FRA $RAD ARRAYET					}				echo ("</select> 								<label for='endTimeHours' class='clearfloat'>Til kl</label> 				<select name='endTimeHours'>");										for($i=0;$i<$timerLength;$i++) {										// FOR LØKKE SOM GÅR LIKE MANGE GANGER SOM TIMETABELLEN ER I LENGDE						echo "<option value='$timer[$i]' "; if($timer[$i] == $bookedEndHour) { echo "selected"; } echo ">$timer[$i]</option>";				// SKRIVER DEN SPESIFIKKE KOLONNEN I TIMETABELLEN					}				echo ("</select> 									<select name='endTimeMinutes'>");									for($i=0;$i<$minLength;$i++) {											// FOR LØKKE SOM GÅR LIKE MANGE GANGER SOM MINUTTTABELLEN ER I LENGDE						echo "<option value='$minutter[$i]' "; if($minutter[$i] == $bookedEndMinute) { echo "selected"; } echo ">$minutter[$i]</option>";		// SKRIVER DEN SPESIFIKKE KOLONNEN I MINUTT-TABELLEN					}				echo ("</select>						<input type='hidden' value='$ref_booking' name=ref_booking id=ref_booking>				<input type='hidden' value='$date' name=date id=date>				<input type='hidden' value='$name' name=name id=name>						<input type='submit' value='Endre' name='editBookingConfirm' id='editBookingConfirm'			</fieldset>		</form>		");			//slutt på form	echo ("		<form method='post' action='bestillinger.php'>		<input type='submit' value='Avbryt' id='back' name='back'>		</form>		");																					//AVBRYTER ENDRINGER OG DIRIGERER TILBAKE TIL BESTILLINGER							$editBookingConfirm = $_POST["editBookingConfirm"];											//LAGER ENDRE-KNAPPif($editBookingConfirm) {																	//LYTTER TIL OM ENDRE-KNAPPEN BLIR TRYKKET	$date			= 	$_POST["date"];														// HENTER INN DATO VARIABEL FRA SKJEMAET	$startTimeClean = 	$_POST["startTimeHours"].":".$_POST["startTimeMinutes"];			// HENTER INN CLEAN STARTTID FRA SKJEMAET(UTEN SEKUNDER)	$endTimeClean	=	$_POST["endTimeHours"].":".$_POST["endTimeMinutes"];				// HENTER INN CLEAN SLUTT-TID FRA SKJEMAET(UTEN SEKUNDER)	$startTime 		= 	$_POST["startTimeHours"].":".$_POST["startTimeMinutes"].":01";		// LAGER STARTTID VARIABEL MED SEKUNDER (SATT TIL 01)	$endTime 		= 	$_POST["endTimeHours"].":".$_POST["endTimeMinutes"].":00";			// LAGER SLUTT-TUD VARIABEL MED SEKUNDER (SATT TIL 00)	$room			=	$_POST["name"];														// HENTER INN ROMMET FRA SKJEMAET	$userRef		=	$_POST["userRef"];													// HENTER USER-REF FRA SKJEMA	$bookingRef		=	createBookingRef();													// KALLER PÅ FUNKSJON OG LAGER BESTILLINGSREFERANSE	$roomId 		= 	getRoomId($room);													// KALLER PÅ EN FUNKSJON FOR Å HENTE ROM ID	$ref_booking	=	$_POST['ref_booking'];												// HENTER REF-BOOKING FRA SKJEMAET	if (!$startTime||!$endTime){															// SJEKKER OM STARTTID OG/ELLER SLUTT-TID IKKE ER BLITT SATT		print ("<h2>Alle feltene må fylles ut</h2>");										// RETURNERER FEILMELDING HVIS KRITERIET OVER ER SANT (DVS. IKKE SATT)	} elseif($startTimeClean == $endTimeClean){												// SJEKKER OM STARTTID OG SLUTT-TID ER LIKE		print ("<h2>Starttid og slutttid kan ikke være like</h2>");							// RETURNERER FEILMELDING HVIS KRITERIET OVER ER SANT	} elseif($startTimeClean > $endTimeClean){												// SJEKKER OM STARTTID ER MINDRE ELLER LIK SLUTT-TID		print ("<h2>Slutttid kan ikke være tidligere enn starttid</h2>");					// RETURNERER FEILMELDING HVIS KRITERIET OVER ER SANT	} elseif($endTimeClean > '20:00'){														// SJEKKER OM SLUTTID ER STØRRE ENN KL. 20:00		print ("<h2>Rommet kan ikke bestilles for lengre enn til 20:00.</h2>");				// RETURNERER FEILMELDING HVIS KRITERIET OVER ER SANT	} elseif(checkIfRoomIsBooked($date, $startTime, $endTime, $roomId)) {					// KALLER PÅ FUNKSJON FOR Å SJEKKE OM ET ROM ER OPPTATT		print ("<h2>Dette rommet er booket innenfor den tidsrammen du har satt, vennligst prøv et annet</h2>");	// RETURNERER FEILMELDING HVIS KRITERIET OVER ER SANT	} elseif(checkUserLeftHours($userRef,$startTimeClean,$endTimeClean,$bookedMinutesChange)){					// KALLER PÅ FUNKSJON FOR Å SJEKKE OM EN BRUKER HAR NOK GJENVÆRENDE TIMER FOR Å BOOKE		print ("<script> alert('Bestillingen overstiger antall timer brukeren totalt kan ha bestilling på'); window.location = 'vis-bestillinger.php';</script>");		// RETURNERER FEILMELDING HVIS KRITERIET OVER ER SANT	} else {		//For å pynte, kan vi her spørre om bruker ble endret. Dersom ikke skal bare tidspunkter oppdateres og ikke ref.nummer	connect();																				//KOBLER TIL DATABASEN	$sql="UPDATE Booking	SET	User_ref='$userRef',	Start_Time='$startTime',	End_Time='$endTime',	Ref_booking='$bookingRef'	WHERE Ref_booking='$ref_booking';";														//SQL-KOMMANDO SOM OPPDATERER DEN AKTUELLE BESTILLINGEN		$result=mysql_query($sql) or die("Ikke mulig å endre bestilling.");						//KJØRER SQL-KOMMANDO	disconnect();																			//KOBLER FRA DATABASEN			print ("<h2>Følgende er nå registrert: Rom $room i tidsrommet " . normalTime($startTime) . "-" . normalTime($endTime) . " bestilt på bruker med referansenummer $userRef. Bestillingsreferansen er $bookingRef.</h2>");		//SKRIVER UT BEKREFTELSE PÅ HVA SOM ER BLITT REGISTRERT	}	echo "<form method='post' action='bestillinger.php'>	<input type='submit' value='Tilbake til bestillinger' id='back' name='back' class='input' />	</form>";}?><?php include '../includes/footerVedlikehold.php'; 	//INKLUDERER FOOTER'EN TIL NETTSTEDET?>